CREATE TABLE "STUDENT_INFO"(
	"STUDENT_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20,
	"STUDENT_NAME" VARCHAR2(50 BYTE) NOT NULL ENABLE,
	"FATHER_NAME" VARCHAR2(50 BYTE) NOT NULL ENABLE,
	"GENDER" VARCHAR2(10 BYTE),
	"MOBILE" LONG,
	"CATEGORY" VARCHAR2(20 BYTE),
	 CONSTRAINT "STUDENT_ID_PK" PRIMARY KEY ("STUDENT_ID"));